import React, { useEffect, useRef } from 'react';
import { Box } from '@mui/material';
import Quill from 'quill';
import "quill/dist/quill.snow.css";

const Editor = () => {
  const editorRef = useRef(null); // Create a ref to attach to the editor

  useEffect(() => {
    if (editorRef.current) { // Ensure the editor div is available
      const quill = new Quill(editorRef.current, {
        theme: 'snow',
        modules: {
          toolbar: [
            [{ header: [1, 2, 3, false] }],
            ['bold', 'italic', 'underline'],
            ['link', 'image'],
            [{ list: 'ordered' }, { list: 'bullet' }],
            ['clean']  // remove formatting button
          ],
        },
      });

      // Cleanup on component unmount
      return () => {
        // This is where you could clean up if needed
        // quill.off(); // Example: Detach any custom event listeners
      };
    }
  }, []);

  return (
    <Box
      ref={editorRef}  // Attach the ref to the Box component
      sx={{
        height: '400px',  // Example height
        border: '1px solid #ccc',  // Add a border
        borderRadius: '5px',  // Slight rounding of corners
        padding: '10px',  // Padding inside the editor
        backgroundColor: '#fff'  // Ensure the background is white
      }}
    ></Box>
  );
}

export default Editor;
