import React, { useEffect } from 'react';
import { Box } from '@mui/material';
import Quill from 'quill';
import "quill/dist/quill.snow.css";

const Editor = () => {
  useEffect(() => {
    const quill = new Quill('#editor', {
      theme: 'snow',
      modules: {
        toolbar: [
          [{ header: [1, 2, 3, false] }],
          ['bold', 'italic', 'underline'],
          ['link', 'image'],
          [{ list: 'ordered'}, { list: 'bullet' }],
          ['clean']  // remove formatting button
        ],
      },
    });

    return () => {
      // If you ever need to cleanup or destroy the Quill instance
      // quill.off(); // This can be used if you attach custom event listeners
    };
  }, []);

  return (
    <Box
      id='editor'
      sx={{
        height: '400px',  // Example height
        border: '1px solid #ccc',  // Add a border
        borderRadius: '5px',  // Slight rounding of corners
        padding: '10px',  // Padding inside the editor
        backgroundColor: '#fff'  // Ensure the background is white
      }}
    ></Box>
  );
}

export default Editor;
